  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  Paper,
  Tooltip,
  Grid,
} from '@mui/material';

import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  AccessTime as AccessTimeIcon,
} from '@mui/icons-material';

import {
  getMyTimeSheets,
  createTimeSheet,
  deleteTimeSheet,
  updateTimeSheet,
} from '../services/timesheetService';
import { useAuth } from '../context/AuthContext';

function StudentDashboard() {
  const { token, user } = useAuth();
  const [timeSheets, setTimeSheets] = useState([]);
  const [loading, setLoading] = useState(true);

  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loadingEdit, setLoadingEdit] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await getMyTimeSheets(token);
      setTimeSheets(res.data);
    } catch {
      alert('โหลด TimeSheet ไม่สำเร็จ');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('ต้องการลบ TimeSheet นี้ใช่หรือไม่?')) return;

    try {
      await deleteTimeSheet(id, token);
      setTimeSheets((prev) => prev.filter((t) => t.id !== id));
    } catch {
      alert('ลบไม่สำเร็จ');
    }
  };

  const [formData, setFormData] = useState({
    date: '',
    checkInTime: '',
    checkOutTime: '',
    activity: '',
  });
  const [formErrors, setFormErrors] = useState({});

  const validateForm = () => {
    const errors = {};
    if (!formData.date) errors.date = 'กรุณาเลือกวันที่';
    if (!formData.checkInTime) errors.checkInTime = 'กรุณากรอกเวลาเข้า';
    if (!formData.checkOutTime) errors.checkOutTime = 'กรุณากรอกเวลาออก';
    if (!formData.activity) errors.activity = 'กรุณากรอกกิจกรรม';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      await createTimeSheet(formData, token);
      fetchData();
      setFormData({ date: '', checkInTime: '', checkOutTime: '', activity: '' });
    } catch {
      alert('บันทึกไม่สำเร็จ');
    }
  };

  const handleEditOpen = (timesheet) => {
    setEditData({
      id: timesheet.id,
      date: timesheet.date.slice(0, 10),
      checkInTime: timesheet.checkInTime.slice(11, 16),
      checkOutTime: timesheet.checkOutTime.slice(11, 16),
      activity: timesheet.activity || '',
    });
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setEditData(null);
    setEditErrors({});
  };

  const [editErrors, setEditErrors] = useState({});

  const validateEditForm = () => {
    const errors = {};
    if (!editData.date) errors.date = 'กรุณาเลือกวันที่';
    if (!editData.checkInTime) errors.checkInTime = 'กรุณากรอกเวลาเข้า';
    if (!editData.checkOutTime) errors.checkOutTime = 'กรุณากรอกเวลาออก';
    if (!editData.activity) errors.activity = 'กรุณากรอกกิจกรรม';
    setEditErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!validateEditForm()) return;

    setLoadingEdit(true);
    try {
      await updateTimeSheet(
        editData.id,
        {
          date: editData.date,
          checkInTime: editData.checkInTime,
          checkOutTime: editData.checkOutTime,
          activity: editData.activity,
        },
        token
      );
      fetchData();
      handleEditClose();
    } catch {
      alert('แก้ไขไม่สำเร็จ');
    }
    setLoadingEdit(false);
  };

  return (
    <>
      <Navbar />
      <Box sx={{ p: 4, maxWidth: 1200, mx: 'auto', backgroundColor: '#f0f2f7', minHeight: '90vh' }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: '#007aff', mb: 4, textAlign: 'center' }}>
          TimeSheet ของ {user?.fullName}
        </Typography>

        <Grid container spacing={4}>
          {/* ฟอร์มเพิ่ม Timesheet */}
          <Grid item xs={12} md={5}>
            <Paper
              elevation={3}
              sx={{
                p: 4,
                bgcolor: '#fff',
                borderRadius: 3,
                boxShadow: '0 8px 20px rgba(0,122,255,0.12)',
              }}
            >
              <Typography variant="h6" sx={{ mb: 3, fontWeight: 600, color: '#004a99' }}>
                เพิ่ม Timesheet ใหม่
              </Typography>
              <form onSubmit={handleSubmit} noValidate>
                <TextField
                  label="วันที่"
                  name="date"
                  type="date"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={formData.date}
                  onChange={handleInputChange}
                  error={Boolean(formErrors.date)}
                  helperText={formErrors.date}
                  sx={{ mb: 2 }}
                />
                <TextField
                  label="เวลาเข้า"
                  name="checkInTime"
                  type="time"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={formData.checkInTime}
                  onChange={handleInputChange}
                  error={Boolean(formErrors.checkInTime)}
                  helperText={formErrors.checkInTime}
                  sx={{ mb: 2 }}
                />
                <TextField
                  label="เวลาออก"
                  name="checkOutTime"
                  type="time"
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  value={formData.checkOutTime}
                  onChange={handleInputChange}
                  error={Boolean(formErrors.checkOutTime)}
                  helperText={formErrors.checkOutTime}
                  sx={{ mb: 2 }}
                />
                <TextField
                  label="กิจกรรม"
                  name="activity"
                  fullWidth
                  multiline
                  rows={3}
                  value={formData.activity}
                  onChange={handleInputChange}
                  error={Boolean(formErrors.activity)}
                  helperText={formErrors.activity}
                  sx={{ mb: 3 }}
                />
                <Button
                  variant="contained"
                  type="submit"
                  fullWidth
                  sx={{
                    textTransform: 'none',
                    backgroundColor: '#007aff',
                    '&:hover': { backgroundColor: '#005bb5', boxShadow: '0 4px 12px rgba(0,91,181,0.4)' },
                    py: 1.5,
                    fontWeight: '600',
                  }}
                  startIcon={<AccessTimeIcon />}
                >
                  บันทึก TimeSheet
                </Button>
              </form>
            </Paper>
          </Grid>

          {/* ตาราง Timesheet */}
          <Grid item xs={12} md={7}>
            <Paper
              elevation={3}
              sx={{
                p: 2,
                bgcolor: '#fff',
                borderRadius: 3,
                boxShadow: '0 8px 20px rgba(0,122,255,0.12)',
                minHeight: 450,
                display: 'flex',
                flexDirection: 'column',
              }}
            >
              {loading ? (
                <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <CircularProgress size={48} color="primary" />
                </Box>
              ) : timeSheets.length === 0 ? (
                <Typography sx={{ mt: 4, textAlign: 'center', color: 'text.disabled' }}>
                  ยังไม่มี TimeSheet
                </Typography>
              ) : (
                <Table sx={{ minWidth: 650 }}>
                  <TableHead>
                    <TableRow sx={{ bgcolor: '#e9f0ff' }}>
                      <TableCell sx={{ fontWeight: 'bold', color: '#004a99' }}>วันที่</TableCell>
                      <TableCell sx={{ fontWeight: 'bold', color: '#004a99' }}>เวลาเข้า</TableCell>
                      <TableCell sx={{ fontWeight: 'bold', color: '#004a99' }}>เวลาออก</TableCell>
                      <TableCell sx={{ fontWeight: 'bold', color: '#004a99' }}>กิจกรรม</TableCell>
                      <TableCell sx={{ fontWeight: 'bold', color: '#004a99', minWidth: 160 }}>จัดการ</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {timeSheets.map((t) => (
                      <TableRow key={t.id} hover>
                        <TableCell>{new Date(t.date).toLocaleDateString()}</TableCell>
                        <TableCell>
                          {new Date(t.checkInTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                        </TableCell>
                        <TableCell>
                          {new Date(t.checkOutTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                        </TableCell>
                        <TableCell sx={{ whiteSpace: 'pre-line' }}>{t.activity}</TableCell>
                        <TableCell>
                          <Tooltip title="แก้ไข">
                            <Button
                              variant="outlined"
                              color="primary"
                              onClick={() => handleEditOpen(t)}
                              sx={{ mr: 1, textTransform: 'none' }}
                              startIcon={<EditIcon />}
                              size="small"
                            >
                              แก้ไข
                            </Button>
                          </Tooltip>
                          <Tooltip title="ลบ">
                            <Button
                              variant="outlined"
                              color="error"
                              onClick={() => handleDelete(t.id)}
                              sx={{ textTransform: 'none' }}
                              startIcon={<DeleteIcon />}
                              size="small"
                            >
                              ลบ
                            </Button>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </Paper>
          </Grid>
        </Grid>

        {/* Dialog แก้ไข Timesheet */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle sx={{ fontWeight: 'bold', color: '#007aff' }}>
            แก้ไข TimeSheet
          </DialogTitle>
          <DialogContent dividers>
            <Box component="form" noValidate autoComplete="off" onSubmit={handleEditSubmit}>
              <TextField
                label="วันที่"
                name="date"
                type="date"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.date || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.date)}
                helperText={editErrors.date}
                sx={{ my: 1 }}
              />
              <TextField
                label="เวลาเข้า"
                name="checkInTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.checkInTime || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.checkInTime)}
                helperText={editErrors.checkInTime}
                sx={{ my: 1 }}
              />
              <TextField
                label="เวลาออก"
                name="checkOutTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.checkOutTime || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.checkOutTime)}
                helperText={editErrors.checkOutTime}
                sx={{ my: 1 }}
              />
              <TextField
                label="กิจกรรม"
                name="activity"
                fullWidth
                multiline
                rows={3}
                value={editData?.activity || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.activity)}
                helperText={editErrors.activity}
                sx={{ my: 1 }}
              />
              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                <Button
                  onClick={handleEditClose}
                  sx={{ mr: 2, textTransform: 'none' }}
                  disabled={loadingEdit}
                >
                  ยกเลิก
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  sx={{
                    textTransform: 'none',
                    backgroundColor: '#007aff',
                    '&:hover': { backgroundColor: '#005bb5', boxShadow: '0 4px 12px rgba(0,91,181,0.4)' },
                  }}
                  disabled={loadingEdit}
                >
                  {loadingEdit ? 'กำลังบันทึก...' : 'บันทึก'}
                </Button>
              </Box>
            </Box>
          </DialogContent>
        </Dialog>
      </Box>
    </>
  );
}

export default StudentDashboard;
