import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Alert,
  InputAdornment,
} from '@mui/material';
import { AccountCircle, Lock } from '@mui/icons-material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { loginUser } from '../services/authService';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const { login } = useAuth();
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      studentId: '',
      password: '',
    },
    validationSchema: Yup.object({
      studentId: Yup.string().required('กรุณากรอกรหัสนักศึกษา'),
      password: Yup.string().required('กรุณากรอกรหัสผ่าน'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors }) => {
      try {
        const res = await loginUser(values);
        login({ token: res.data.token, user: res.data.user });

        if (res.data.user.role === 'admin') {
          navigate('/admin');
        } else if (res.data.user.role === 'student') {
          navigate('/student');
        } else {
          navigate('/');
        }
      } catch (error) {
        setErrors({ submit: 'รหัสนักศึกษาหรือรหัสผ่านไม่ถูกต้อง' });
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <Box
      sx={{
        minHeight: '100vh',
        // สีพื้นหลังเขียวเข้ม พร้อมลวดลาย subtle
        backgroundColor: '#0b3d0b',
        backgroundImage:
          'radial-gradient(rgba(255,255,255,0.05) 1px, transparent 1px), radial-gradient(rgba(255,255,255,0.05) 1px, transparent 1px)',
        backgroundPosition: '0 0, 25px 25px',
        backgroundSize: '50px 50px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: 3,
      }}
    >
      <Box
        sx={{
          maxWidth: 420,
          width: '100%',
          bgcolor: '#121d12', // สีพื้นกล่องเข้มๆเขียวดำ
          p: 5,
          borderRadius: 3,
          boxShadow: '0 6px 18px rgba(0,0,0,0.7)',
          color: '#d0f0c0',
        }}
      >
        <Typography
          variant="h4"
          sx={{
            fontWeight: 700,
            color: '#a6d785',
            textAlign: 'center',
            mb: 1.5,
            letterSpacing: 1,
            textShadow: '0 0 8px #8bc34a',
          }}
        >
          ระบบ Timesheet สำหรับนักศึกษา
        </Typography>

        <Typography
          variant="h6"
          sx={{
            fontWeight: 500,
            color: '#c3e6a0',
            textAlign: 'center',
            mb: 4,
          }}
        >
          เข้าสู่ระบบ
        </Typography>

        <form onSubmit={formik.handleSubmit} noValidate>
          <TextField
            label="รหัสนักศึกษา"
            name="studentId"
            fullWidth
            margin="normal"
            variant="filled"
            color="success"
            value={formik.values.studentId}
            onChange={formik.handleChange}
            error={formik.touched.studentId && Boolean(formik.errors.studentId)}
            helperText={formik.touched.studentId && formik.errors.studentId}
            autoFocus
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <AccountCircle sx={{ color: '#a6d785' }} />
                </InputAdornment>
              ),
              sx: {
                bgcolor: '#1b3a1b',
                color: '#d0f0c0',
                borderRadius: 1,
              },
            }}
          />

          <TextField
            label="รหัสผ่าน"
            name="password"
            type="password"
            fullWidth
            margin="normal"
            variant="filled"
            color="success"
            value={formik.values.password}
            onChange={formik.handleChange}
            error={formik.touched.password && Boolean(formik.errors.password)}
            helperText={formik.touched.password && formik.errors.password}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Lock sx={{ color: '#a6d785' }} />
                </InputAdornment>
              ),
              sx: {
                bgcolor: '#1b3a1b',
                color: '#d0f0c0',
                borderRadius: 1,
              },
            }}
          />

          {formik.errors.submit && (
            <Alert severity="error" sx={{ mt: 2, bgcolor: '#611414', color: '#ffdcdc' }}>
              {formik.errors.submit}
            </Alert>
          )}

          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{
              mt: 4,
              py: 1.5,
              fontWeight: 700,
              backgroundColor: '#66bb6a',
              color: '#122a12',
              textShadow: '0 0 6px #2e7d32',
              '&:hover': {
                backgroundColor: '#4caf50',
                boxShadow: '0 0 15px #4caf50',
              },
            }}
            disabled={formik.isSubmitting}
          >
            เข้าสู่ระบบ
          </Button>

          <Button
            fullWidth
            sx={{
              mt: 2,
              textTransform: 'none',
              color: '#a6d785',
              fontWeight: 600,
              '&:hover': { textDecoration: 'underline' },
            }}
            onClick={() => navigate('/register')}
          >
            ลงทะเบียน
          </Button>
        </form>
      </Box>
    </Box>
  );
};

export default LoginPage;
