import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  Paper,
  Tooltip,
  IconButton,
  useMediaQuery,
  useTheme,
} from '@mui/material';

import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  AccessTime as AccessTimeIcon,
} from '@mui/icons-material';

import {
  getMyTimeSheets,
  createTimeSheet,
  deleteTimeSheet,
  updateTimeSheet,
} from '../services/timesheetService';
import { useAuth } from '../context/AuthContext';
import Swal from 'sweetalert2';

function StudentDashboard() {
  const { token, user } = useAuth();
  const [timeSheets, setTimeSheets] = useState([]);
  const [loading, setLoading] = useState(true);

  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loadingEdit, setLoadingEdit] = useState(false);

  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm')); // sm < 600px

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await getMyTimeSheets(token);
      setTimeSheets(res.data);
    } catch {
      Swal.fire('ผิดพลาด', 'ไม่สามารถโหลด Timesheet ได้', 'error');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: 'คุณแน่ใจหรือไม่?',
      text: 'คุณต้องการลบ TimeSheet นี้ใช่หรือไม่',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'ใช่, ลบเลย!',
      cancelButtonText: 'ยกเลิก',
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
    });

    if (result.isConfirmed) {
      try {
        await deleteTimeSheet(id, token);
        setTimeSheets((prev) => prev.filter((t) => t.id !== id));
        Swal.fire('ลบสำเร็จ', 'TimeSheet ได้ถูกลบแล้ว', 'success');
      } catch {
        Swal.fire('ผิดพลาด', 'ไม่สามารถลบ TimeSheet ได้', 'error');
      }
    }
  };

  const [formData, setFormData] = useState({
    date: '',
    checkInTime: '',
    checkOutTime: '',
    activity: '',
  });
  const [formErrors, setFormErrors] = useState({});

  const validateForm = () => {
    const errors = {};
    if (!formData.date) errors.date = 'กรุณาเลือกวันที่';
    if (!formData.checkInTime) errors.checkInTime = 'กรุณากรอกเวลาเข้า';
    if (!formData.checkOutTime) errors.checkOutTime = 'กรุณากรอกเวลาออก';
    if (!formData.activity) errors.activity = 'กรุณากรอกกิจกรรม';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  if (!validateForm()) return;

  try {
    const payload = {
      date: formData.date,
      checkInTime: formData.checkInTime,
      checkOutTime: formData.checkOutTime,
      activity: formData.activity,
    };

    console.log('Payload to create:', payload);

    await createTimeSheet(payload, token);
    fetchData();
    setFormData({ date: '', checkInTime: '', checkOutTime: '', activity: '' });
    Swal.fire('สำเร็จ', 'เพิ่ม TimeSheet เรียบร้อยแล้ว', 'success');
  } catch {
    Swal.fire('ผิดพลาด', 'ไม่สามารถเพิ่ม TimeSheet ได้', 'error');
  }
};


  const handleEditOpen = (timesheet) => {
    setEditData({
      id: timesheet.id,
      date: timesheet.date.slice(0, 10),
      checkInTime: timesheet.checkInTime.slice(11, 16),
      checkOutTime: timesheet.checkOutTime.slice(11, 16),
      activity: timesheet.activity || '',
    });
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setEditData(null);
    setEditErrors({});
  };

  const [editErrors, setEditErrors] = useState({});

  const validateEditForm = () => {
    const errors = {};
    if (!editData.date) errors.date = 'กรุณาเลือกวันที่';
    if (!editData.checkInTime) errors.checkInTime = 'กรุณากรอกเวลาเข้า';
    if (!editData.checkOutTime) errors.checkOutTime = 'กรุณากรอกเวลาออก';
    if (!editData.activity) errors.activity = 'กรุณากรอกกิจกรรม';
    setEditErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!validateEditForm()) return;

    setLoadingEdit(true);
    try {
      // แปลงวัน+เวลาเป็น ISO string พร้อม timezone +07:00 (เวลาไทย)
      const date = editData.date;
      const checkInISO = new Date(`${date}T${editData.checkInTime}:00+07:00`).toISOString();
      const checkOutISO = new Date(`${date}T${editData.checkOutTime}:00+07:00`).toISOString();

      await updateTimeSheet(
        editData.id,
        {
          date,
          checkInTime: checkInISO,
          checkOutTime: checkOutISO,
          activity: editData.activity,
        },
        token
      );
      await fetchData();
      handleEditClose();
      Swal.fire('บันทึกสำเร็จ', 'แก้ไข TimeSheet เรียบร้อยแล้ว', 'success');
    } catch {
      Swal.fire('ผิดพลาด', 'ไม่สามารถแก้ไข TimeSheet ได้', 'error');
    }
    setLoadingEdit(false);
  };

  return (
    <>
      <Navbar />
      <Box
        sx={{
          minHeight: '90vh',
          backgroundColor: '#f5f7fa',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          pt: isSmallScreen ? 3 : 6,
          px: 2,
          pb: 6,
          maxWidth: 700,
          mx: 'auto',
        }}
      >
        <Typography
          variant={isSmallScreen ? 'h5' : 'h4'}
          sx={{
            fontWeight: '700',
            color: '#0066cc',
            mb: 4,
            textAlign: 'center',
            letterSpacing: 1,
          }}
        >
          TimeSheet ของ {user?.fullName}
        </Typography>

        {/* ฟอร์มเพิ่ม Timesheet */}
        <Paper
          elevation={4}
          sx={{
            width: '100%',
            mb: 5,
            p: isSmallScreen ? 2 : 4,
            borderRadius: 3,
            backgroundColor: '#fff',
            boxShadow: '0 8px 24px rgba(0,102,204,0.15)',
          }}
        >
          <Typography
            variant="h6"
            sx={{ mb: 3, fontWeight: 600, color: '#004a99' }}
          >
            เพิ่ม Timesheet ใหม่
          </Typography>
          <form onSubmit={handleSubmit} noValidate>
            <TextField
              label="วันที่"
              name="date"
              type="date"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formData.date}
              onChange={handleInputChange}
              error={Boolean(formErrors.date)}
              helperText={formErrors.date}
              sx={{ mb: 3 }}
            />
            <TextField
              label="เวลาเข้า"
              name="checkInTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formData.checkInTime}
              onChange={handleInputChange}
              error={Boolean(formErrors.checkInTime)}
              helperText={formErrors.checkInTime}
              sx={{ mb: 3 }}
            />
            <TextField
              label="เวลาออก"
              name="checkOutTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formData.checkOutTime}
              onChange={handleInputChange}
              error={Boolean(formErrors.checkOutTime)}
              helperText={formErrors.checkOutTime}
              sx={{ mb: 3 }}
            />
            <TextField
              label="กิจกรรม"
              name="activity"
              fullWidth
              multiline
              rows={3}
              value={formData.activity}
              onChange={handleInputChange}
              error={Boolean(formErrors.activity)}
              helperText={formErrors.activity}
              sx={{ mb: 3 }}
            />
            <Button
              variant="contained"
              type="submit"
              fullWidth
              sx={{
                textTransform: 'none',
                backgroundColor: '#0066cc',
                '&:hover': {
                  backgroundColor: '#004a99',
                  boxShadow: '0 6px 20px rgba(0,74,153,0.3)',
                },
                py: 1.5,
                fontWeight: '700',
                fontSize: 16,
              }}
              startIcon={<AccessTimeIcon />}
            >
              บันทึก TimeSheet
            </Button>
          </form>
        </Paper>

        {/* ตาราง Timesheet */}
        <Paper
          elevation={4}
          sx={{
            width: '100%',
            borderRadius: 3,
            p: isSmallScreen ? 1 : 2,
            backgroundColor: '#fff',
            boxShadow: '0 8px 24px rgba(0,102,204,0.15)',
            overflowX: 'auto',

            // scrollbar styles for webkit browsers
            '&::-webkit-scrollbar': {
              height: 8,
            },
            '&::-webkit-scrollbar-track': {
              backgroundColor: '#f0f0f0',
              borderRadius: 4,
            },
            '&::-webkit-scrollbar-thumb': {
              backgroundColor: '#0066cc',
              borderRadius: 4,
            },

            // scrollbar styles for Firefox
            scrollbarWidth: 'thin',
            scrollbarColor: '#0066cc #f0f0f0',
          }}
        >
          {loading ? (
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: 200,
              }}
            >
              <CircularProgress size={48} color="primary" />
            </Box>
          ) : timeSheets.length === 0 ? (
            <Typography
              sx={{ textAlign: 'center', color: 'text.disabled', py: 8 }}
              variant="subtitle1"
            >
              ยังไม่มี TimeSheet
            </Typography>
          ) : (
            <Table sx={{ minWidth: 650 }}>
              <TableHead>
                <TableRow sx={{ bgcolor: '#e3f2fd' }}>
                  <TableCell sx={{ fontWeight: 'bold', color: '#004a99', fontSize: isSmallScreen ? 12 : 14 }}>
                    วันที่
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: '#004a99', fontSize: isSmallScreen ? 12 : 14 }}>
                    เวลาเข้า
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: '#004a99', fontSize: isSmallScreen ? 12 : 14 }}>
                    เวลาออก
                  </TableCell>
                  <TableCell
                    sx={{
                      fontWeight: 'bold',
                      color: '#004a99',
                      fontSize: isSmallScreen ? 12 : 14,
                      whiteSpace: 'nowrap',
                      maxWidth: 200,
                    }}
                  >
                    กิจกรรม
                  </TableCell>
                  <TableCell
                    sx={{
                      fontWeight: 'bold',
                      color: '#004a99',
                      minWidth: 140,
                      fontSize: isSmallScreen ? 12 : 14,
                    }}
                  >
                    จัดการ
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {timeSheets.map((t) => (
                  <TableRow key={t.id} hover>
                    <TableCell sx={{ fontSize: isSmallScreen ? 12 : 14 }}>
                      {new Date(t.date).toLocaleDateString('th-TH')}
                    </TableCell>
                    <TableCell sx={{ fontSize: isSmallScreen ? 12 : 14 }}>
                      {new Date(t.checkInTime).toLocaleTimeString('th-TH', {
                        hour: '2-digit',
                        minute: '2-digit',
                        hour12: false,
                        timeZone: 'Asia/Bangkok',
                      })}
                    </TableCell>
                    <TableCell sx={{ fontSize: isSmallScreen ? 12 : 14 }}>
                      {new Date(t.checkOutTime).toLocaleTimeString('th-TH', {
                        hour: '2-digit',
                        minute: '2-digit',
                        hour12: false,
                        timeZone: 'Asia/Bangkok',
                      })}
                    </TableCell>
                    <TableCell
                      sx={{
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-word',
                        maxWidth: 200,
                        fontSize: isSmallScreen ? 12 : 14,
                      }}
                    >
                      {t.activity}
                    </TableCell>
                    <TableCell>
                      <Tooltip title="แก้ไข">
                        <IconButton
                          color="primary"
                          onClick={() => handleEditOpen(t)}
                          size={isSmallScreen ? 'small' : 'medium'}
                        >
                          <EditIcon fontSize={isSmallScreen ? 'small' : 'medium'} />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="ลบ">
                        <IconButton
                          color="error"
                          onClick={() => handleDelete(t.id)}
                          size={isSmallScreen ? 'small' : 'medium'}
                        >
                          <DeleteIcon fontSize={isSmallScreen ? 'small' : 'medium'} />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </Paper>

        {/* Dialog แก้ไข Timesheet */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle sx={{ fontWeight: 'bold', color: '#0066cc' }}>
            แก้ไข TimeSheet
          </DialogTitle>
          <DialogContent dividers>
            <Box
              component="form"
              noValidate
              autoComplete="off"
              onSubmit={handleEditSubmit}
              sx={{
                '& .MuiTextField-root': {
                  my: 1,
                },
              }}
            >
              <TextField
                label="วันที่"
                name="date"
                type="date"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.date || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.date)}
                helperText={editErrors.date}
              />
              <TextField
                label="เวลาเข้า"
                name="checkInTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.checkInTime || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.checkInTime)}
                helperText={editErrors.checkInTime}
              />
              <TextField
                label="เวลาออก"
                name="checkOutTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editData?.checkOutTime || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.checkOutTime)}
                helperText={editErrors.checkOutTime}
              />
              <TextField
                label="กิจกรรม"
                name="activity"
                fullWidth
                multiline
                rows={3}
                value={editData?.activity || ''}
                onChange={handleEditChange}
                error={Boolean(editErrors.activity)}
                helperText={editErrors.activity}
              />
              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                <Button
                  onClick={handleEditClose}
                  sx={{ mr: 2, textTransform: 'none' }}
                  disabled={loadingEdit}
                >
                  ยกเลิก
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  sx={{
                    textTransform: 'none',
                    backgroundColor: '#0066cc',
                    '&:hover': {
                      backgroundColor: '#004a99',
                      boxShadow: '0 6px 20px rgba(0,74,153,0.3)',
                    },
                  }}
                  disabled={loadingEdit}
                >
                  {loadingEdit ? 'กำลังบันทึก...' : 'บันทึก'}
                </Button>
              </Box>
            </Box>
          </DialogContent>
        </Dialog>
      </Box>
    </>
  );
}

export default StudentDashboard;
