import axios from 'axios';

const API_BASE = 'http://localhost:5000/api'; // เปลี่ยนตาม URL backend คุณ

export const getUserProfile = (token) =>
  axios.get(`${API_BASE}/api/profile`, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const updateUserProfile = (data, token) =>
  axios.put(`${API_BASE}/user/profile`, data, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const changePassword = (currentPassword, newPassword, token) =>
  axios.put(
    `${API_BASE}/user/change-password`,
    { currentPassword, newPassword },
    {
      headers: { Authorization: `Bearer ${token}` },
    }
  );
