import React, { useEffect, useState } from 'react';
import {
  Box, Typography, TextField, Button, Table, TableHead, TableRow,
  TableCell, TableBody, Dialog, DialogTitle, DialogContent, DialogActions
} from '@mui/material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { getMyTimeSheets, createTimeSheet, deleteTimeSheet, updateTimeSheet } from '../services/timesheetService';

function StudentDashboard() {
  // ... state และฟังก์ชันเดิม

  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);

  // ฟังก์ชันเปิด dialog แก้ไข พร้อมข้อมูลเดิม
  const handleEditOpen = (timesheet) => {
    setEditData(timesheet);
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setEditData(null);
  };

  // Formik สำหรับแก้ไข
  const editFormik = useFormik({
    enableReinitialize: true,  // สำคัญให้โหลดค่าใหม่เมื่อ editData เปลี่ยน
    initialValues: {
      date: editData ? editData.date.slice(0,10) : '',
      checkInTime: editData ? editData.checkInTime.slice(11,16) : '',
      checkOutTime: editData ? editData.checkOutTime.slice(11,16) : '',
      activity: editData ? editData.activity : '',
    },
    validationSchema: Yup.object({
      date: Yup.string().required('กรุณาเลือกวันที่'),
      checkInTime: Yup.string().required('กรุณากรอกเวลาเข้า'),
      checkOutTime: Yup.string().required('กรุณากรอกเวลาออก'),
      activity: Yup.string().required('กรุณากรอกกิจกรรม'),
    }),
    onSubmit: async (values) => {
      try {
        await updateTimeSheet(editData.id, {
          date: values.date,
          checkInTime: values.checkInTime,
          checkOutTime: values.checkOutTime,
          activity: values.activity,
        }, token);
        fetchData();
        handleEditClose();
      } catch {
        alert('แก้ไขไม่สำเร็จ');
      }
    },
  });

  return (
    <>
      {/* โค้ดเดิม */}
      <Table>
        {/* ... header ... */}
        <TableBody>
          {timeSheets.map(t => (
            <TableRow key={t.id}>
              <TableCell>{new Date(t.date).toLocaleDateString()}</TableCell>
              <TableCell>{new Date(t.checkInTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</TableCell>
              <TableCell>{new Date(t.checkOutTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</TableCell>
              <TableCell>{t.activity}</TableCell>
              <TableCell>
                <Button variant="outlined" color="primary" onClick={() => handleEditOpen(t)}>แก้ไข</Button>
                <Button variant="outlined" color="error" onClick={() => handleDelete(t.id)}>ลบ</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      {/* Dialog แก้ไข */}
      <Dialog open={editOpen} onClose={handleEditClose}>
        <DialogTitle>แก้ไข TimeSheet</DialogTitle>
        <DialogContent>
          <form onSubmit={editFormik.handleSubmit} id="edit-timesheet-form">
            <TextField
              label="วันที่"
              name="date"
              type="date"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={editFormik.values.date}
              onChange={editFormik.handleChange}
              error={editFormik.touched.date && Boolean(editFormik.errors.date)}
              helperText={editFormik.touched.date && editFormik.errors.date}
              sx={{ mb: 2 }}
            />
            <TextField
              label="เวลาเข้า"
              name="checkInTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={editFormik.values.checkInTime}
              onChange={editFormik.handleChange}
              error={editFormik.touched.checkInTime && Boolean(editFormik.errors.checkInTime)}
              helperText={editFormik.touched.checkInTime && editFormik.errors.checkInTime}
              sx={{ mb: 2 }}
            />
            <TextField
              label="เวลาออก"
              name="checkOutTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={editFormik.values.checkOutTime}
              onChange={editFormik.handleChange}
              error={editFormik.touched.checkOutTime && Boolean(editFormik.errors.checkOutTime)}
              helperText={editFormik.touched.checkOutTime && editFormik.errors.checkOutTime}
              sx={{ mb: 2 }}
            />
            <TextField
              label="กิจกรรม"
              name="activity"
              fullWidth
              multiline
              rows={3}
              value={editFormik.values.activity}
              onChange={editFormik.handleChange}
              error={editFormik.touched.activity && Boolean(editFormik.errors.activity)}
              helperText={editFormik.touched.activity && editFormik.errors.activity}
              sx={{ mb: 2 }}
            />
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>ยกเลิก</Button>
          <Button type="submit" form="edit-timesheet-form" variant="contained">บันทึก</Button>
        </DialogActions>
      </Dialog>
    </>
  );
}
