import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  CircularProgress,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Tooltip,
  IconButton,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { getStudentTimesheetById, updateStudentTimesheetById, deleteStudentTimesheetById } from '../services/adminService';
import Navbar from '../components/Navbar';
import { useAuth } from '../context/AuthContext';
import Swal from 'sweetalert2';

function StudentTimesheetView() {
  const { id } = useParams();
  const { token } = useAuth();
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));

  const [data, setData] = useState([]);
  const [studentInfo, setStudentInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState({
    id: '',
    date: '',
    checkInTime: '',
    checkOutTime: '',
    activity: '',
  });
  const [saving, setSaving] = useState(false);
  const [editErrors, setEditErrors] = useState({});

  const fetchTimesheet = useCallback(async () => {
    setLoading(true);
    try {
      const res = await getStudentTimesheetById(id, token);
      setData(res.data.timesheets || []);
      setStudentInfo(res.data.student || null);
    } catch (err) {
      Swal.fire('ผิดพลาด', 'โหลดข้อมูล Timesheet ไม่สำเร็จ', 'error');
    } finally {
      setLoading(false);
    }
  }, [id, token]);

  useEffect(() => {
    fetchTimesheet();
  }, [fetchTimesheet]);

  const handleEditOpen = (timesheet) => {
    setEditErrors({});
    setEditData({
      id: timesheet.id,
      date: timesheet.date ? timesheet.date.slice(0, 10) : '',
      checkInTime: timesheet.checkInTime ? timesheet.checkInTime.slice(11, 16) : '',
      checkOutTime: timesheet.checkOutTime ? timesheet.checkOutTime.slice(11, 16) : '',
      activity: timesheet.activity || '',
    });
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setSaving(false);
    setEditErrors({});
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData((prev) => ({ ...prev, [name]: value }));
  };

  // ฟังก์ชันตรวจสอบความถูกต้องฟอร์มก่อนบันทึก
  const validateEditForm = () => {
    const errors = {};
    if (!editData.date) errors.date = 'กรุณาเลือกวันที่';
    if (!editData.checkInTime) errors.checkInTime = 'กรุณากรอกเวลาเข้า';
    if (!editData.checkOutTime) errors.checkOutTime = 'กรุณากรอกเวลาออก';
    if (!editData.activity) errors.activity = 'กรุณากรอกกิจกรรม';

    // ตรวจสอบว่าเวลาออกต้องมากกว่าเวลาเข้า
    if (editData.checkInTime && editData.checkOutTime) {
      const checkIn = new Date(`${editData.date}T${editData.checkInTime}:00`);
      const checkOut = new Date(`${editData.date}T${editData.checkOutTime}:00`);
      if (checkOut <= checkIn) {
        errors.checkOutTime = 'เวลาออกต้องมากกว่าเวลาเข้า';
      }
    }
    setEditErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleEditSave = async () => {
    if (!validateEditForm()) return;

    setSaving(true);
    try {
      // แปลงเวลาเป็น ISO string แบบ UTC (เพื่อส่งให้ backend)
      const checkIn = new Date(`${editData.date}T${editData.checkInTime}:00`);
      const checkOut = new Date(`${editData.date}T${editData.checkOutTime}:00`);

      await updateStudentTimesheetById(
        editData.id,
        {
          date: editData.date,
          checkInTime: checkIn.toISOString(),
          checkOutTime: checkOut.toISOString(),
          activity: editData.activity,
        },
        token
      );
      Swal.fire('สำเร็จ', 'แก้ไข Timesheet เรียบร้อยแล้ว', 'success');
      setEditOpen(false);
      fetchTimesheet();
    } catch (error) {
      Swal.fire('ผิดพลาด', 'ไม่สามารถแก้ไข Timesheet ได้', 'error');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (timesheetId) => {
    const result = await Swal.fire({
      title: 'คุณแน่ใจหรือไม่?',
      text: 'คุณต้องการลบ TimeSheet นี้ใช่หรือไม่',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'ใช่, ลบเลย!',
      cancelButtonText: 'ยกเลิก',
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
    });

    if (result.isConfirmed) {
      try {
        await deleteStudentTimesheetById(timesheetId, token);
        setData((prev) => prev.filter((t) => t.id !== timesheetId));
        Swal.fire('ลบสำเร็จ', 'TimeSheet ได้ถูกลบแล้ว', 'success');
      } catch {
        Swal.fire('ผิดพลาด', 'ไม่สามารถลบ Timesheet ได้', 'error');
      }
    }
  };

  return (
    <>
      <Navbar />
      <Box
        sx={{
          minHeight: '100vh',
          backgroundColor: '#f4f6f8',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'start',
          px: isSmallScreen ? 1 : 2,
          py: 4,
        }}
      >
        <Box sx={{ width: '100%', maxWidth: 1000 }}>
          <Typography variant={isSmallScreen ? 'h6' : 'h5'} sx={{ fontWeight: 'bold', mb: 2, color: '#333', textAlign: 'center' }}>
            ข้อมูล TimeSheet ของนักศึกษา
          </Typography>

          {studentInfo && (
            <Typography variant={isSmallScreen ? 'subtitle1' : 'h6'} sx={{ color: '#555', textAlign: 'center', mb: 3 }}>
              {studentInfo.fullName} (รหัส {studentInfo.studentId})
            </Typography>
          )}

          {loading ? (
            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <CircularProgress size={48} />
            </Box>
          ) : data.length === 0 ? (
            <Typography align="center" color="text.secondary" sx={{ mt: 4 }}>
              ยังไม่มีข้อมูล TimeSheet
            </Typography>
          ) : (
            <Paper
              elevation={1}
              sx={{
                overflowX: 'auto',
                overflowY: 'auto',
                maxHeight: 500,
                borderRadius: 2,
                border: '1px solid #e0e0e0',
                backgroundColor: '#fff',
                scrollbarWidth: 'thin',
                '&::-webkit-scrollbar': {
                  height: 8,
                  backgroundColor: '#f5f5f5',
                },
                '&::-webkit-scrollbar-thumb': {
                  backgroundColor: '#0066cc',
                  borderRadius: 4,
                },
              }}
            >
              <Table>
                <TableHead sx={{ backgroundColor: '#f5f5f5' }}>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 'bold', minWidth: 120 }}>วันที่</TableCell>
                    <TableCell sx={{ fontWeight: 'bold', minWidth: 100 }}>เวลาเข้า</TableCell>
                    <TableCell sx={{ fontWeight: 'bold', minWidth: 100 }}>เวลาออก</TableCell>
                    <TableCell sx={{ fontWeight: 'bold', minWidth: 300 }}>กิจกรรม</TableCell>
                    <TableCell sx={{ fontWeight: 'bold', minWidth: 110 }}>จัดการ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {data.map((t) => (
                    <TableRow key={t.id} hover>
                      <TableCell>
                        {new Date(t.date).toLocaleDateString('th-TH', {
                          day: 'numeric',
                          month: 'short',
                          year: 'numeric',
                        })}
                      </TableCell>
                      <TableCell>
                        {t.checkInTime
                          ? new Date(t.checkInTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                          : '-'}
                      </TableCell>
                      <TableCell>
                        {t.checkOutTime
                          ? new Date(t.checkOutTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                          : '-'}
                      </TableCell>
                      <TableCell
                        sx={{
                          whiteSpace: 'pre-wrap',
                          wordBreak: 'break-word',
                          maxWidth: 300,
                          fontSize: isSmallScreen ? 12 : 14,
                        }}
                      >
                        {t.activity}
                      </TableCell>
                      <TableCell>
                        <Tooltip title="แก้ไข">
                          <IconButton color="primary" size={isSmallScreen ? 'small' : 'medium'} onClick={() => handleEditOpen(t)}>
                            <EditIcon fontSize={isSmallScreen ? 'small' : 'medium'} />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="ลบ">
                          <IconButton color="error" size={isSmallScreen ? 'small' : 'medium'} onClick={() => handleDelete(t.id)}>
                            <DeleteIcon fontSize={isSmallScreen ? 'small' : 'medium'} />
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Paper>
          )}

          {/* Dialog แก้ไข Timesheet */}
          <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
            <DialogTitle>แก้ไข Timesheet</DialogTitle>
            <DialogContent>
              <TextField
                label="วันที่"
                name="date"
                type="date"
                fullWidth
                value={editData.date}
                onChange={handleEditChange}
                sx={{ mb: 2 }}
                InputLabelProps={{ shrink: true }}
                error={!!editErrors.date}
                helperText={editErrors.date}
              />
              <TextField
                label="เวลาเข้า"
                name="checkInTime"
                type="time"
                fullWidth
                value={editData.checkInTime}
                onChange={handleEditChange}
                sx={{ mb: 2 }}
                InputLabelProps={{ shrink: true }}
                error={!!editErrors.checkInTime}
                helperText={editErrors.checkInTime}
              />
              <TextField
                label="เวลาออก"
                name="checkOutTime"
                type="time"
                fullWidth
                value={editData.checkOutTime}
                onChange={handleEditChange}
                sx={{ mb: 2 }}
                InputLabelProps={{ shrink: true }}
                error={!!editErrors.checkOutTime}
                helperText={editErrors.checkOutTime}
              />
              <TextField
                label="กิจกรรม"
                name="activity"
                fullWidth
                multiline
                rows={3}
                value={editData.activity}
                onChange={handleEditChange}
                error={!!editErrors.activity}
                helperText={editErrors.activity}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleEditClose} disabled={saving}>
                ยกเลิก
              </Button>
              <Button variant="contained" onClick={handleEditSave} disabled={saving}>
                {saving ? 'กำลังบันทึก...' : 'บันทึก'}
              </Button>
            </DialogActions>
          </Dialog>
        </Box>
      </Box>
    </>
  );
}

export default StudentTimesheetView;
