import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import {
  Box,
  Typography,
  TextField,
  Button,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Paper,
  CircularProgress,
} from '@mui/material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import {
  getMyTimeSheets,
  createTimeSheet,
  deleteTimeSheet,
  updateTimeSheet,
} from '../services/timesheetService';
import { useAuth } from '../context/AuthContext';

function StudentDashboard() {
  const { token, user } = useAuth();
  const [timeSheets, setTimeSheets] = useState([]);
  const [loading, setLoading] = useState(true);

  // สำหรับ dialog แก้ไข
  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [loadingEdit, setLoadingEdit] = useState(false);

  // โหลด timesheets
  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await getMyTimeSheets(token);
      setTimeSheets(res.data);
    } catch (err) {
      alert('โหลด TimeSheet ไม่สำเร็จ');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  // ลบ timesheet
  const handleDelete = async (id) => {
    if (!window.confirm('ต้องการลบ TimeSheet นี้ใช่หรือไม่?')) return;

    try {
      await deleteTimeSheet(id, token);
      setTimeSheets((prev) => prev.filter((t) => t.id !== id));
    } catch {
      alert('ลบไม่สำเร็จ');
    }
  };

  // ฟอร์มบันทึก timesheet ใหม่
  const formik = useFormik({
    initialValues: {
      date: '',
      checkInTime: '',
      checkOutTime: '',
      activity: '',
    },
    validationSchema: Yup.object({
      date: Yup.string().required('กรุณาเลือกวันที่'),
      checkInTime: Yup.string().required('กรุณากรอกเวลาเข้า'),
      checkOutTime: Yup.string().required('กรุณากรอกเวลาออก'),
      activity: Yup.string().required('กรุณากรอกกิจกรรม'),
    }),
    onSubmit: async (values, { resetForm }) => {
      try {
        await createTimeSheet(values, token);
        fetchData();
        resetForm();
      } catch {
        alert('บันทึกไม่สำเร็จ');
      }
    },
  });

  // เปิด dialog แก้ไข พร้อมข้อมูลเดิม
  const handleEditOpen = (timesheet) => {
    setEditData(timesheet);
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setEditData(null);
  };

  // ฟอร์มแก้ไข timesheet (enableReinitialize สำคัญ!)
  const editFormik = useFormik({
    enableReinitialize: true,
    initialValues: {
      date: editData ? editData.date.slice(0, 10) : '',
      checkInTime: editData ? editData.checkInTime.slice(11, 16) : '',
      checkOutTime: editData ? editData.checkOutTime.slice(11, 16) : '',
      activity: editData ? editData.activity || '' : '',
    },
    validationSchema: Yup.object({
      date: Yup.string().required('กรุณาเลือกวันที่'),
      checkInTime: Yup.string().required('กรุณากรอกเวลาเข้า'),
      checkOutTime: Yup.string().required('กรุณากรอกเวลาออก'),
      activity: Yup.string().required('กรุณากรอกกิจกรรม'),
    }),
    onSubmit: async (values) => {
      setLoadingEdit(true);
      try {
        await updateTimeSheet(
          editData.id,
          {
            date: values.date,
            checkInTime: values.checkInTime,
            checkOutTime: values.checkOutTime,
            activity: values.activity,
          },
          token
        );
        await fetchData();
        handleEditClose();
      } catch {
        alert('แก้ไขไม่สำเร็จ');
      }
      setLoadingEdit(false);
    },
  });

  return (
    <>
      <Navbar />
      <Box
        sx={{
          p: 4,
          maxWidth: 900,
          mx: 'auto',
          bgcolor: 'background.default',
          minHeight: '100vh',
        }}
      >
        <Typography
          variant="h4"
          gutterBottom
          sx={{ fontWeight: 'bold', color: 'primary.main' }}
        >
          TimeSheet ของ {user?.fullName}
        </Typography>

        <Paper
          elevation={3}
          sx={{ p: 3, mb: 4, borderRadius: 2, boxShadow: 4 }}
        >
          <form onSubmit={formik.handleSubmit}>
            <Typography
              variant="h6"
              sx={{ mb: 2, fontWeight: 'medium', color: 'text.primary' }}
            >
              เพิ่ม TimeSheet ใหม่
            </Typography>

            <TextField
              label="วันที่"
              name="date"
              type="date"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formik.values.date}
              onChange={formik.handleChange}
              error={formik.touched.date && Boolean(formik.errors.date)}
              helperText={formik.touched.date && formik.errors.date}
              sx={{ mb: 2 }}
            />
            <TextField
              label="เวลาเข้า"
              name="checkInTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formik.values.checkInTime}
              onChange={formik.handleChange}
              error={formik.touched.checkInTime && Boolean(formik.errors.checkInTime)}
              helperText={formik.touched.checkInTime && formik.errors.checkInTime}
              sx={{ mb: 2 }}
            />
            <TextField
              label="เวลาออก"
              name="checkOutTime"
              type="time"
              fullWidth
              InputLabelProps={{ shrink: true }}
              value={formik.values.checkOutTime}
              onChange={formik.handleChange}
              error={formik.touched.checkOutTime && Boolean(formik.errors.checkOutTime)}
              helperText={formik.touched.checkOutTime && formik.errors.checkOutTime}
              sx={{ mb: 2 }}
            />
            <TextField
              label="กิจกรรม"
              name="activity"
              fullWidth
              multiline
              rows={3}
              value={formik.values.activity}
              onChange={formik.handleChange}
              error={formik.touched.activity && Boolean(formik.errors.activity)}
              helperText={formik.touched.activity && formik.errors.activity}
              sx={{ mb: 2 }}
            />

            <Button
              variant="contained"
              type="submit"
              fullWidth
              sx={{ mt: 1, textTransform: 'none' }}
            >
              บันทึก TimeSheet
            </Button>
          </form>
        </Paper>

        <Typography
          variant="h6"
          gutterBottom
          sx={{ color: 'text.secondary', mb: 1 }}
        >
          รายการ TimeSheet
        </Typography>

        {loading ? (
          <Box sx={{ textAlign: 'center', mt: 4 }}>
            <CircularProgress color="primary" />
          </Box>
        ) : timeSheets.length === 0 ? (
          <Typography
            sx={{ mt: 4, textAlign: 'center', color: 'text.disabled' }}
          >
            ยังไม่มี TimeSheet
          </Typography>
        ) : (
          <Paper
            elevation={3}
            sx={{ overflowX: 'auto', boxShadow: 4, borderRadius: 2 }}
          >
            <Table sx={{ minWidth: 650 }}>
              <TableHead>
                <TableRow sx={{ bgcolor: 'primary.light' }}>
                  <TableCell sx={{ fontWeight: 'bold', color: 'primary.contrastText' }}>
                    วันที่
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: 'primary.contrastText' }}>
                    เวลาเข้า
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: 'primary.contrastText' }}>
                    เวลาออก
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: 'primary.contrastText' }}>
                    กิจกรรม
                  </TableCell>
                  <TableCell sx={{ fontWeight: 'bold', color: 'primary.contrastText', minWidth: 140 }}>
                    จัดการ
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {timeSheets.map((t) => (
                  <TableRow key={t.id} hover>
                    <TableCell>{new Date(t.date).toLocaleDateString()}</TableCell>
                    <TableCell>
                      {new Date(t.checkInTime).toLocaleTimeString([], {
                        hour: '2-digit',
                        minute: '2-digit',
                      })}
                    </TableCell>
                    <TableCell>
                      {new Date(t.checkOutTime).toLocaleTimeString([], {
                        hour: '2-digit',
                        minute: '2-digit',
                      })}
                    </TableCell>
                    <TableCell sx={{ whiteSpace: 'pre-line' }}>{t.activity}</TableCell>
                    <TableCell>
                      <Button
                        variant="outlined"
                        color="primary"
                        onClick={() => handleEditOpen(t)}
                        sx={{ mr: 1, textTransform: 'none' }}
                      >
                        แก้ไข
                      </Button>
                      <Button
                        variant="outlined"
                        color="error"
                        onClick={() => handleDelete(t.id)}
                        sx={{ textTransform: 'none' }}
                      >
                        ลบ
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        )}

        {/* Dialog แก้ไข */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle sx={{ fontWeight: 'bold', color: 'primary.main' }}>
            แก้ไข TimeSheet
          </DialogTitle>
          <DialogContent>
            <form
              id="edit-timesheet-form"
              onSubmit={editFormik.handleSubmit}
              style={{ marginTop: 8 }}
            >
              <TextField
                label="วันที่"
                name="date"
                type="date"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editFormik.values.date}
                onChange={editFormik.handleChange}
                error={editFormik.touched.date && Boolean(editFormik.errors.date)}
                helperText={editFormik.touched.date && editFormik.errors.date}
                sx={{ mb: 2 }}
              />
              <TextField
                label="เวลาเข้า"
                name="checkInTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editFormik.values.checkInTime}
                onChange={editFormik.handleChange}
                error={editFormik.touched.checkInTime && Boolean(editFormik.errors.checkInTime)}
                helperText={editFormik.touched.checkInTime && editFormik.errors.checkInTime}
                sx={{ mb: 2 }}
              />
              <TextField
                label="เวลาออก"
                name="checkOutTime"
                type="time"
                fullWidth
                InputLabelProps={{ shrink: true }}
                value={editFormik.values.checkOutTime}
                onChange={editFormik.handleChange}
                error={editFormik.touched.checkOutTime && Boolean(editFormik.errors.checkOutTime)}
                helperText={editFormik.touched.checkOutTime && editFormik.errors.checkOutTime}
                sx={{ mb: 2 }}
              />
              <TextField
                label="กิจกรรม"
                name="activity"
                fullWidth
                multiline
                rows={3}
                value={editFormik.values.activity}
                onChange={editFormik.handleChange}
                error={editFormik.touched.activity && Boolean(editFormik.errors.activity)}
                helperText={editFormik.touched.activity && editFormik.errors.activity}
                sx={{ mb: 2 }}
              />
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditClose} disabled={loadingEdit} sx={{ textTransform: 'none' }}>
              ยกเลิก
            </Button>
            <Button
              form="edit-timesheet-form"
              type="submit"
              variant="contained"
              disabled={loadingEdit}
              sx={{ textTransform: 'none' }}
            >
              {loadingEdit ? 'กำลังบันทึก...' : 'บันทึก'}
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </>
  );
}

export default StudentDashboard;
