import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  CircularProgress,
} from '@mui/material';
import { useAuth } from '../context/AuthContext';
import TimeSheetForm from './TimeSheetForm';
import TimeSheetEditDialog from './TimeSheetEditDialog';
import {
  getMyTimeSheets,
  deleteTimeSheet,
  updateTimeSheet,
} from '../services/timesheetService';

function StudentDashboard() {
  const { token, user } = useAuth();
  const [timeSheets, setTimeSheets] = useState([]);
  const [loading, setLoading] = useState(true);

  // Edit dialog state
  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);

  // โหลด Timesheets
  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await getMyTimeSheets(token);
      setTimeSheets(res.data);
    } catch {
      alert('โหลด TimeSheet ไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // ลบ timesheet
  const handleDelete = async (id) => {
    if (!window.confirm('ต้องการลบ TimeSheet นี้ใช่หรือไม่?')) return;
    try {
      await deleteTimeSheet(id, token);
      setTimeSheets(prev => prev.filter(t => t.id !== id));
    } catch {
      alert('ลบไม่สำเร็จ');
    }
  };

  // เปิด dialog แก้ไข
  const handleEditOpen = (timesheet) => {
    setEditData(timesheet);
    setEditOpen(true);
  };

  // ปิด dialog แก้ไข
  const handleEditClose = () => {
    setEditOpen(false);
    setEditData(null);
  };

  // บันทึกข้อมูลแก้ไข timesheet
  const handleEditSave = async (values, resetLoading) => {
    try {
      await updateTimeSheet(editData.id, values, token);
      alert('แก้ไข TimeSheet สำเร็จ');
      await fetchData();
      handleEditClose();
    } catch {
      alert('แก้ไขไม่สำเร็จ');
    }
    resetLoading(false);
  };

  return (
    <>
      <Navbar />
      <Box sx={{ p: 4 }}>
        <Typography variant="h4" gutterBottom>
          TimeSheet ของ {user?.fullName}
        </Typography>

        {/* ฟอร์มเพิ่ม Timesheet ใหม่ */}
        <TimeSheetForm token={token} fetchData={fetchData} />

        <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>
          รายการ TimeSheet
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : (
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>วันที่</TableCell>
                <TableCell>เวลาเข้า</TableCell>
                <TableCell>เวลาออก</TableCell>
                <TableCell>กิจกรรม</TableCell>
                <TableCell>จัดการ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {timeSheets.map((t) => (
                <TableRow key={t.id}>
                  <TableCell>{new Date(t.date).toLocaleDateString()}</TableCell>
                  <TableCell>
                    {new Date(t.checkInTime).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </TableCell>
                  <TableCell>
                    {new Date(t.checkOutTime).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </TableCell>
                  <TableCell>{t.activity}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      onClick={() => handleEditOpen(t)}
                      sx={{ mr: 1 }}
                    >
                      แก้ไข
                    </Button>
                    <Button
                      variant="outlined"
                      color="error"
                      onClick={() => handleDelete(t.id)}
                    >
                      ลบ
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}

        {/* Dialog แก้ไข */}
        <TimeSheetEditDialog
          open={editOpen}
          onClose={handleEditClose}
          initialData={editData}
          onSave={handleEditSave}
        />
      </Box>
    </>
  );
}

export default StudentDashboard;
