import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  CircularProgress,
  Paper,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  Button,
  Tooltip,
} from '@mui/material';
import {
  Edit as EditIcon,
  Delete as DeleteIcon,
} from '@mui/icons-material';
import Swal from 'sweetalert2';
import { getStudentTimesheetById, deleteTimesheetById, updateTimesheetById } from '../services/adminService';
import Navbar from '../components/Navbar';
import { useAuth } from '../context/AuthContext';

function StudentTimesheetView() {
  const { id } = useParams();
  const { token } = useAuth();

  const [data, setData] = useState([]);
  const [studentInfo, setStudentInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [editErrors, setEditErrors] = useState({});

  const fetchTimesheet = async () => {
    try {
      const res = await getStudentTimesheetById(id, token);
      setData(res.data.timesheets || []);
      setStudentInfo(res.data.student || null);
    } catch {
      Swal.fire('Error', 'โหลดข้อมูล Timesheet ไม่สำเร็จ', 'error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTimesheet();
  }, [id, token]);

  const handleEditOpen = (row) => {
    setEditData({
      id: row.id,
      date: row.date.slice(0, 10),
      checkInTime: row.checkInTime.slice(11, 16),
      checkOutTime: row.checkOutTime.slice(11, 16),
      activity: row.activity,
    });
    setEditOpen(true);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditData((prev) => ({ ...prev, [name]: value }));
  };

  const validateEdit = () => {
    const errors = {};
    if (!editData.date) errors.date = 'กรุณาระบุวันที่';
    if (!editData.checkInTime) errors.checkInTime = 'กรุณาระบุเวลาเข้า';
    if (!editData.checkOutTime) errors.checkOutTime = 'กรุณาระบุเวลาออก';
    if (!editData.activity) errors.activity = 'กรุณากรอกกิจกรรม';
    setEditErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!validateEdit()) return;

    try {
      await updateTimesheetById(editData.id, editData, token);
      Swal.fire('สำเร็จ', 'แก้ไขข้อมูลเรียบร้อย', 'success');
      setEditOpen(false);
      fetchTimesheet();
    } catch {
      Swal.fire('ผิดพลาด', 'ไม่สามารถแก้ไขได้', 'error');
    }
  };

  const handleDelete = async (tsId) => {
    const confirm = await Swal.fire({
      title: 'ลบ TimeSheet?',
      text: 'คุณแน่ใจหรือไม่ว่าต้องการลบข้อมูลนี้',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      confirmButtonText: 'ลบ',
      cancelButtonText: 'ยกเลิก',
    });

    if (!confirm.isConfirmed) return;

    try {
      await deleteTimesheetById(tsId, token);
      Swal.fire('ลบสำเร็จ', '', 'success');
      fetchTimesheet();
    } catch {
      Swal.fire('ลบไม่สำเร็จ', '', 'error');
    }
  };

  return (
    <>
      <Navbar />
      <Box sx={{ minHeight: '100vh', backgroundColor: '#f4f6f8', px: 2, py: 4, display: 'flex', justifyContent: 'center' }}>
        <Box sx={{ width: '100%', maxWidth: 1000 }}>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 2, textAlign: 'center' }}>
            ข้อมูล TimeSheet ของนักศึกษา
          </Typography>

          {studentInfo && (
            <Typography variant="h6" sx={{ textAlign: 'center', mb: 3 }}>
              {studentInfo.fullName} (รหัส {studentInfo.studentId})
            </Typography>
          )}

          {loading ? (
            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <CircularProgress size={48} />
            </Box>
          ) : data.length === 0 ? (
            <Typography align="center" color="text.secondary" sx={{ mt: 4 }}>
              ยังไม่มีข้อมูล TimeSheet
            </Typography>
          ) : (
            <Paper sx={{ overflowX: 'auto', borderRadius: 2, border: '1px solid #e0e0e0' }}>
              <Table>
                <TableHead sx={{ backgroundColor: '#f5f5f5' }}>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 'bold' }}>วันที่</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>เวลาเข้า</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>เวลาออก</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>กิจกรรม</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>จัดการ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {data.map((t) => (
                    <TableRow key={t.id} hover>
                      <TableCell>{new Date(t.date).toLocaleDateString()}</TableCell>
                      <TableCell>{new Date(t.checkInTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</TableCell>
                      <TableCell>{new Date(t.checkOutTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</TableCell>
                      <TableCell sx={{ whiteSpace: 'pre-line' }}>{t.activity}</TableCell>
                      <TableCell>
                        <Tooltip title="แก้ไข">
                          <IconButton color="primary" onClick={() => handleEditOpen(t)}>
                            <EditIcon />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="ลบ">
                          <IconButton color="error" onClick={() => handleDelete(t.id)}>
                            <DeleteIcon />
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Paper>
          )}

          {/* Edit Dialog */}
          <Dialog open={editOpen} onClose={() => setEditOpen(false)} maxWidth="sm" fullWidth>
            <DialogTitle>แก้ไข TimeSheet</DialogTitle>
            <DialogContent dividers>
              <Box component="form" onSubmit={handleEditSubmit}>
                <TextField
                  label="วันที่"
                  type="date"
                  name="date"
                  fullWidth
                  margin="dense"
                  value={editData?.date || ''}
                  onChange={handleEditChange}
                  InputLabelProps={{ shrink: true }}
                  error={!!editErrors.date}
                  helperText={editErrors.date}
                />
                <TextField
                  label="เวลาเข้า"
                  type="time"
                  name="checkInTime"
                  fullWidth
                  margin="dense"
                  value={editData?.checkInTime || ''}
                  onChange={handleEditChange}
                  InputLabelProps={{ shrink: true }}
                  error={!!editErrors.checkInTime}
                  helperText={editErrors.checkInTime}
                />
                <TextField
                  label="เวลาออก"
                  type="time"
                  name="checkOutTime"
                  fullWidth
                  margin="dense"
                  value={editData?.checkOutTime || ''}
                  onChange={handleEditChange}
                  InputLabelProps={{ shrink: true }}
                  error={!!editErrors.checkOutTime}
                  helperText={editErrors.checkOutTime}
                />
                <TextField
                  label="กิจกรรม"
                  name="activity"
                  multiline
                  rows={3}
                  fullWidth
                  margin="dense"
                  value={editData?.activity || ''}
                  onChange={handleEditChange}
                  error={!!editErrors.activity}
                  helperText={editErrors.activity}
                />
                <Box sx={{ textAlign: 'right', mt: 2 }}>
                  <Button onClick={() => setEditOpen(false)} sx={{ mr: 2 }}>ยกเลิก</Button>
                  <Button type="submit" variant="contained">บันทึก</Button>
                </Box>
              </Box>
            </DialogContent>
          </Dialog>
        </Box>
      </Box>
    </>
  );
}

export default StudentTimesheetView;
