import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { getMyTimesheets, updateTimesheet, deleteTimesheet } from '../services/timesheetService';
import { useAuth } from '../context/AuthContext';

function StudentDashboard() {
  const { token } = useAuth();
  const [timesheets, setTimesheets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editOpen, setEditOpen] = useState(false);
  const [selectedTimesheet, setSelectedTimesheet] = useState(null);
  const [formData, setFormData] = useState({
    date: '',
    checkInTime: '',
    checkOutTime: '',
    activity: '',
  });

  // ฟังก์ชันแปลง DateTime เป็น HH:mm
  const formatTime = (dateString) => {
    const date = new Date(dateString);
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  // โหลด Timesheet ของนักศึกษา
  const fetchTimesheets = async () => {
    try {
      const res = await getMyTimesheets(token);
      setTimesheets(res.data);
    } catch (err) {
      alert('ไม่สามารถโหลดข้อมูล Timesheet ได้');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTimesheets();
  }, []);

  // เปิด modal แก้ไข และ set ข้อมูลในฟอร์ม (แปลงเวลาเป็น HH:mm)
  const handleEditOpen = (timesheet) => {
    setSelectedTimesheet(timesheet);
    setFormData({
      date: timesheet.date.split('T')[0], // YYYY-MM-DD
      checkInTime: formatTime(timesheet.checkInTime),
      checkOutTime: formatTime(timesheet.checkOutTime),
      activity: timesheet.activity || '',
    });
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setSelectedTimesheet(null);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // บันทึกข้อมูลที่แก้ไข
  const handleSave = async () => {
    try {
      // เตรียมเวลาแบบ ISO string
      const checkInDateTime = `${formData.date}T${formData.checkInTime}:00`;
      const checkOutDateTime = `${formData.date}T${formData.checkOutTime}:00`;

      await updateTimesheet(selectedTimesheet.id, {
        date: formData.date,
        checkInTime: checkInDateTime,
        checkOutTime: checkOutDateTime,
        activity: formData.activity,
      }, token);

      alert('แก้ไข Timesheet สำเร็จ');
      setEditOpen(false);
      fetchTimesheets();
    } catch (err) {
      alert('แก้ไข Timesheet ไม่สำเร็จ');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('ต้องการลบ Timesheet นี้ใช่หรือไม่?')) return;

    try {
      await deleteTimesheet(id, token);
      setTimesheets((prev) => prev.filter((t) => t.id !== id));
    } catch {
      alert('ลบ Timesheet ไม่สำเร็จ');
    }
  };

  return (
    <>
      <Box sx={{ p: 4 }}>
        <Typography variant="h4" gutterBottom>
          Timesheet ของฉัน
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : (
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>วันที่</TableCell>
                <TableCell>เวลาเข้า</TableCell>
                <TableCell>เวลาออก</TableCell>
                <TableCell>กิจกรรม</TableCell>
                <TableCell>จัดการ</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {timesheets.map((t) => (
                <TableRow key={t.id}>
                  <TableCell>{t.date.split('T')[0]}</TableCell>
                  <TableCell>{formatTime(t.checkInTime)}</TableCell>
                  <TableCell>{formatTime(t.checkOutTime)}</TableCell>
                  <TableCell>{t.activity || '-'}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      sx={{ mr: 1 }}
                      onClick={() => handleEditOpen(t)}
                    >
                      แก้ไข
                    </Button>
                    <Button
                      color="error"
                      variant="outlined"
                      onClick={() => handleDelete(t.id)}
                    >
                      ลบ
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        )}

        {/* Modal แก้ไข Timesheet */}
        <Dialog open={editOpen} onClose={handleEditClose} maxWidth="sm" fullWidth>
          <DialogTitle>แก้ไข Timesheet</DialogTitle>
          <DialogContent>
            <TextField
              label="วันที่"
              type="date"
              name="date"
              fullWidth
              margin="normal"
              value={formData.date}
              onChange={handleChange}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              label="เวลาเข้า"
              type="time"
              name="checkInTime"
              fullWidth
              margin="normal"
              value={formData.checkInTime}
              onChange={handleChange}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              label="เวลาออก"
              type="time"
              name="checkOutTime"
              fullWidth
              margin="normal"
              value={formData.checkOutTime}
              onChange={handleChange}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              label="กิจกรรม"
              name="activity"
              fullWidth
              margin="normal"
              value={formData.activity}
              onChange={handleChange}
              multiline
              rows={3}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditClose}>ยกเลิก</Button>
            <Button variant="contained" onClick={handleSave}>
              บันทึก
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </>
  );
}

export default StudentDashboard;
